INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'productplan', '按产品统计的年度新增计划数', 'count_of_annual_created_productplan_in_product', '个', '按产品统计的年度新增计划数是指在某年度，产品团队新加入的计划数量。这个度量项可以反映产品团队对于新需求和新项目的接收能力和规模的扩展。新增计划数越多，说明产品团队在该年度内面临着更多的新挑战和需求。', '产品中创建时间为某年的计划个数求和\n过滤已删除的计划\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
