INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'release', '按产品统计的发布总数', 'count_of_release_in_product', '个', '按产品统计的发布总数是指产品中所有发布的数量。这个度量项可以反映产品团队对产品发布的频率和稳定性的掌控程度。发布总数越多，说明产品团队在时间段内有更多的迭代和产品更新。', '产品中发布的个数求和\n过滤已删除的发布\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'release', '按产品统计的年度新增发布数', 'count_of_annual_created_release_in_product', '个', '按产品统计的年度新增发布数是指在某年度产品中新增加的发布数量。这个度量项可以反映产品团队在该年度内对新功能和改进的发布能力和速度。新增发布数越多，说明产品团队在该年度内推出了更多的新功能和改进。', '产品中发布时间为某年的发布个数求和\n过滤已删除的发布\n过滤已删除的产品\n过滤无效时间', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('qc', 'product', 'release', '按产品统计的发布上线后的Bug数', 'count_of_released_bug_in_product', '个', '按产品统计的发布上线后的Bug数是指在产品发布到线上后，发现的Bug数量。这个度量项可以反映产品团队在发布后的质量控制能力和产品稳定性。发布上线后的Bug数越少，说明产品团队在发布前经过了充分的测试和优化，产品质量和用户体验更好。', '产品中Bug创建时间为某个发布上线后且关联到这个发布版本的Bug个数求和\n过滤已删除的计划\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'story', '按产品统计的研发需求总数', 'count_of_story_in_product', '个', '按产品统计的研发需求总数是指产品中创建的所有研发需求的数量。这个度量项可以反映团队需进行研发工作的规模和复杂度。研发需求总数越多，意味着团队在该时间段内面临着更多的开发要求。', '产品中研发需求的个数求和\n过滤已删除的研发需求\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'story', '按产品统计的已完成研发需求数', 'count_of_finished_story_in_product', '个', '按产品统计的已完成研发需求数是指状态为已关闭且关闭原因为已完成的研发需求的数量。这个度量项可以反映产品团队在开发过程中的进展和交付能力。已完成研发需求数越多，说明产品团队在该时间段内取得了更多的开发成果。', '产品中所处阶段为已关闭且关闭原因为已完成的研发需求个数求和\n过滤已删除的研发需求\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
