INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'story', '按产品统计的已交付研发需求数', 'count_of_delivered_story_in_product', '个', '按产品统计的已交付研发需求数表示已交付给用户的研发需求的数量。该度量项反映了产品中已经交付给用户的研发需求的数量，可以用于评估产品的研发需求交付能力和合作效果。', '产品中所处阶段为已发布或关闭原因为已完成的研发需求个数求和\n过滤已删除的研发需求\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
