INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'story', '按产品统计的月度新增研发需求数', 'count_of_monthly_created_story_in_product', '个', '按产品统计的月度新增研发需求数是指在某月度新增的研发需求数量。这个度量项可以反映产品团队在该月度内需求的增长或变化情况。', '产品中创建时间在某年某月的研发需求的个数求和\n过滤已删除的研发需求\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'story', '按产品统计的所有研发需求规模数', 'scale_of_story_in_product', 'sp/工时/功能点', '按产品统计的所有研发需求规模数表示研发需求的规模总数。该度量项反映了产品研发需求的规模总数，可以用于评估产品团队的研发需求规模管理和成果。', '产品中研发需求的规模数求和\n过滤已删除的研发需求\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'story', '按产品统计的年度已完成研发需求规模数', 'scale_of_annual_finished_story_in_product', 'sp/工时/功能点', '按产品统计的已完成研发需求规模数表示已经完成的研发需求的规模总数。该度量项反映了产品已经完成的研发需求的规模总数，可以用于评估产品团队的研发需求规模管理和成果。', '产品中关闭时间在某年且关闭原因为已完成的研发需求的规模数求和\n过滤已删除的研发需求\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'story', '按产品统计的年度已交付研发需求规模数', 'scale_of_annual_delivered_story_in_product', 'sp/工时', '按产品统计的年度交付研发需求数表示每年交付的研发需求的数量。该度量项反映了产品每年交付给用户的研发需求数量，可以用于评估产品的研发需求交付效能和效果。', '产品中所处阶段为已发布且发布时间为某年某月或关闭原因为已完成且关闭时间为某年某月的研发需求规模数求和\n过滤已删除的研发需求\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'story', '按产品统计的年度关闭研发需求规模数', 'scale_of_annual_closed_story_in_product', 'sp/工时', '按产品统计的年度关闭研发需求规模数表示每年关闭的研发需求的规模总数。该度量项反映了产品每年关闭的研发需求的规模总数，可以用于评估产品的研发需求规模管理和调整情况。', '产品中关闭时间在某年的研发需求的规模数求和\n过滤已删除的研发需求\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('qc', 'product', 'story', '按产品统计的研发需求评审通过率', 'rate_of_approved_story_in_product', '%', '按产品统计的研发需求评审通过率表示产品中不需要评审的与评审通过的研发需求数相对于不需要评审的与有评审结果的需求数的比例。该度量项反映了需求评审过程中的成功率。', '按产品统计的所有研发需求评审通过率=（按产品统计的不需要评审的研发需求数+评审结果确认通过的研发需求数）/（按产品统计的不需要评审的研发需求数+有评审结果的研发需求数）\n过滤已删除的研发需求\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'bug', '按产品统计的研发完成需求的Bug密度', 'bug_concentration_of_developed_story_in_product', '个', '按产品统计的研发完成需求的Bug密度表示按产品统计的研发完成需求的有效Bug数相对于按产品统计的研发完成的研发需求规模数。该度量项反映了研发阶段中需求质量的表现，密度越低代表研发完成的需求质量越高。', '复用：\n按产品统计的有效Bug数\n按产品统计的研发完成的研发需求规模数\n公式：\n按产品统计的研发完成需求的Bug密度=按产品统计的有效Bug数/按产品统计的研发完成的研发需求规模数', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'requirement', '按产品统计的用户需求总数', 'count_of_requirement_in_product', '个', '按产品统计的用户需求总数是指产品所有用户需求的总数。该度量项反映了对用户需求量的整体把握和了解程度。', '产品中用户需求的个数求和\n过滤已删除的用户需求\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'requirement', '按产品统计的年度新增用户需求数', 'count_of_annual_created_requirement_in_product', '个', '产品年度新增用户需求总数', '产品中创建时间为某年的用户需求的个数求和\n过滤已删除的用户需求\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'feedback', '按产品统计的反馈总数', 'count_of_feedback_in_product', '个', '产品的反馈总数', '产品中反馈的个数求和\n过滤已删除的反馈\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'feedback', '按产品统计的年度新增反馈数', 'count_of_annual_created_feedback_in_product', '个', '按产品统计的年度新增反馈数是指在某年度收集到的用户反馈的数量。这个度量项可以帮助团队了解用户对产品的发展趋势和需求变化，并进行产品策略的调整和优化。较高的年度新增反馈数可能暗示着产品的用户基础扩大或者功能迭代带来了更多用户参与，同时暗示产品问题可能有很多。', '产品中创建时间为某年的反馈的个数求和\n过滤已删除的反馈\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'feedback', '按产品统计的年度关闭反馈数', 'count_of_annual_closed_feedback_in_product', '个', '按产品统计的年度关闭反馈数是指在某年度处理并关闭的用户反馈的数量。这个度量项可以帮助产品团队评估在某年度对用户反馈的响应能力和问题解决能力。较高的年度关闭反馈数可能暗示着团队能够高效地解决用户反馈并持续改进产品，提升用户满意度和产品质量。', '产品中关闭时间为某年的反馈的个数求和\n过滤已删除的反馈\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'bug', '按产品统计的Bug总数', 'count_of_bug_in_product', '个', '按产品统计的Bug总数是指在产品中发现的所有Bug的数量。这个度量项反映了产品整体Bug质量情况。Bug总数越多可能代表产品的代码质量存在问题，需要进行进一步的解决和改进。', '产品中Bug的个数求和\n过滤已删除的Bug\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'bug', '按产品统计的激活Bug数', 'count_of_activated_bug_in_product', '个', '按产品统计的激活Bug数是指当前未解决的Bug数量。这个度量项反映了产品当前存在的待解决问题数量。激活Bug总数越多可能代表产品的稳定性较低，需要加强Bug解决的速度和质量。', '产品中激活Bug的个数求和\n过滤已删除的Bug\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'bug', '按产品统计的有效Bug数', 'count_of_effective_bug_in_product', '个', '按产品统计的有效Bug数是指产品中真正具有影响和价值的Bug数量。有效Bug通常是指导致产品不正常运行或影响用户体验的Bug。统计有效Bug数可以帮助评估产品的稳定性和质量也可以评估测试人员之前的协作或对产品的了解程度。', '有效Bug数=方案为已解决的Bug数+方案为延期处理的Bug数+激活的Bug数\n过滤已删除的Bug\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'bug', '按产品统计的已修复Bug数', 'count_of_fixed_bug_in_product', '个', '按产品统计的已修复Bug数是指解决并关闭的Bug数量。这个度量项反映了产品解决的问题数量。已修复Bug数的增加说明开发团队在Bug解决方面的工作效率较高。', '产品中Bug的个数求和\n解决方案为已解决\n状态为已关闭\n过滤已删除的Bug\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'bug', '按产品统计的严重程度为1级的Bug数', 'count_of_severity_1_bug_in_product', '个', '按产品统计的严重程度为1级的Bug数是指在产品开发过程中发现的、对产品功能或性能产生重大影响的Bug数量。这些Bug可能会导致系统崩溃、功能无法正常运行、数据丢失等严重问题。统计这些Bug的数量可以帮助评估产品的稳定性和可靠性。', '产品的严重程度为1级的Bug数\n过滤已删除的Bug\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'bug', '按产品统计的严重程度为2级的Bug数', 'count_of_severity_2_bug_in_product', '个', '按产品统计的严重程度为2级的Bug数是指在产品开发过程中发现的、对产品功能或性能产生较大影响的Bug数量。这些Bug可能会给用户带来不便或影响产品的某些功能。', '产品的严重程度为2级的Bug数\n过滤已删除的Bug\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'Bug', '按产品统计的严重程度为1、2级的Bug数', 'count_of_severe_bug_in_product', '个', '按产品统计的严重程度为1、2级的Bug数是指在产品开发过程中发现的严重程度为1级和2级的Bug数量的总和。统计这些Bug的数量可以评估产品的质量和稳定性，同时也关注影响用户体验和功能完整性的问题。', '复用：\n按产品统计的严重程度为1级的Bug数\n按产品统计的严重程度为2级的Bug数\n公式：\n按产品统计的严重程度为1、2级的Bug数=按产品统计的严重程度为1级的Bug数+按产品统计的严重程度为2级的Bug数', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'Bug', '按产品统计的年度新增Bug数', 'count_of_annual_created_bug_in_product', '个', '按产品统计的年度新增Bug数是指在某年度新发现的Bug数量。这个度量项反映了系统或项目在某年度出现的新问题数量。年度新增Bug数的增加可能意味着质量控制存在问题，需要及时进行处理和改进。', '产品中创建时间为某年的Bug的个数求和\n过滤已删除的Bug\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'Bug', '按产品统计的年度新增有效Bug数', 'count_of_annual_created_effective_bug_in_product', '个', '按产品统计的年度新增有效Bug数是指产品在某年度新发现的真正具有影响和价值的Bug数量。有效Bug通常是指导致产品不正常运行或影响用户体验的Bug。统计有效Bug数可以帮助评估产品的稳定性和质量也可以评估测试人员之前的协作或对产品的了解程度。', '产品中创建时间为某年的解决方案为已解决和延期处理的或者状态为激活的Bug的个数求和\n过滤已删除的Bug\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'Bug', '按产品统计的年度修复Bug数', 'count_of_annual_fixed_bug_in_product', '个', '按产品统计的年度修复Bug数是指在某年度解决并关闭的Bug数量。这个度量项反映了产品在某年度解决的问题数量。年度修复Bug数的增加说明开发团队在Bug解决方面的工作效率较高。', '产品中关闭时间为某年且解决方案为已解决的Bug的个数求和\n过滤已删除的Bug\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'product', 'Bug', '按产品统计的每日新增Bug数', 'count_of_daily_created_bug_in_product', '个', '按产品统计的每日新增Bug数是指在每天的产品开发过程中新发现并记录的Bug数量。该度量项可以体现产品开发过程中Bug的发现速度和趋势，较高的新增Bug数可能意味着存在较多的问题需要解决，同时也可以帮助识别产品开发过程中的瓶颈和潜在的质量风险。', '产品中每日创建的Bug数求和\n过滤已删除的Bug\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
