INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'global', 'bug', '按全局统计的年度新增Bug数', 'count_of_annual_created_bug', '个', '按全局统计的年度新增Bug数是指在一年内新发现的Bug数量。这个度量项反映了系统或项目在一年内出现的新缺陷数量。年度新增Bug数的增加可能意味着质量控制存在问题，需要及时进行处理和改进。', '所有Bug个数求和\n创建时间为某年\n过滤已删除的Bug\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'global', 'bug', '按全局统计的年度修复Bug数', 'count_of_annual_fixed_bug', '个', '按全局统计的年度修复Bug数是指在一年内解决并关闭的Bug数量。这个度量项反映了系统或项目在一年内解决的缺陷数量。年度修复Bug数的增加说明开发团队在Bug解决方面的工作效率较高。', '所有Bug个数求和\n状态为已关闭\n解决方案为已解决\n关闭时间为某年\n过滤已删除的Bug\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'global', 'bug', '按全局统计的月度新增Bug数', 'count_of_monthly_created_bug', '个', '按全局统计的月度新增Bug数是指在一个月内新发现的Bug数量。这个度量项反映了系统或项目在一个月内出现的新缺陷数量。月度新增Bug数的增加可能需要关注质量控制和测试过程中的问题。 ', '所有Bug个数求和\n创建时间为某年某月\n过滤已删除的Bug\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'global', 'bug', '按全局统计的月度修复Bug数', 'count_of_monthly_fixed_bug', '个', '按全局统计的月度修复Bug数是指在一个月内解决并关闭的Bug数量。这个度量项反映了系统或项目在一个月内解决的问题数量。月度修复Bug数的增加说明开发团队的工作效率较高。', '所有Bug个数求和\n状态为已关闭\n解决方案为已解决\n关闭时间为某年某月\n过滤已删除的Bug\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('rate', 'global', 'bug', '按全局统计的Bug修复率', 'rate_of_fixed_bug', '%', '按全局统计的Bug修复率是指已修复的Bug占相对于有效Bug数量的比例。这个度量项反映了系统或项目在解决Bug方面的成功率。Bug修复率越高说明开发团队在解决问题上的能力越强，同时也需要关注解决的质量。', '复用：\n按全局统计的已修复Bug数\n按全局统计的有效Bug数\n公式：\n按全局统计的Bug修复率=按全局统计的已修复Bug数/按全局统计的有效Bug数', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'global', 'testcase', '按全局统计的用例总数', 'count_of_case', '个', '按全局统计的用例总数是指系统或项目中的测试用例总数量。用例是用来验证系统功能和性能的测试场景。统计用例总数可以帮助评估测试覆盖的广度和深度。用例总数越高可能意味着项目进行了全面和充分的测试。', '所有用例个数求和\n过滤已删除的用例\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'global', 'testcase', '按全局统计的年度新增用例数', 'count_of_annual_created_case', '个', '按全局统计的年度新增用例数是指在一年内新增的测试用例数量。统计年度新增用例数可以帮助评估系统或项目在不同阶段的测试覆盖和测试深度。年度新增用例数的增加可能意味着对新功能和需求进行了更充分的测试。', '所有用例个数求和\n创建时间在某年\n过滤已删除的用例\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'global', 'user', '按全局统计的用户总数', 'count_of_user', '个', '按全局统计的人员总数是指在项目或系统中参与开发和管理的人员总数。该度量项可以帮助评估项目的规模和团队规模。人员总数的增加可能意味着项目的扩大或资源投入的增加。', '系统所有用户个数求和\n过滤已删除的用户', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'global', 'user', '按全局统计的年度添加用户数', 'count_of_annual_created_user', '个', '按全局统计的年度新增人员数是指在一年内新增加到项目或系统中的人员数量。该度量项可以用来评估团队扩充和人员流动情况。年度新增人员数的增加可能意味着团队的增加或项目的扩大。', '系统所有用户个数求和\n添加时间为某年', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('hour', 'global', 'effort', '按全局统计的年度日志记录的工时总数', 'hour_of_annual_effort', 'h', '按全局统计的年度日志记录的工时总数是指组织在某年度实际花费的总工时数。该度量项可以用来评估组织的工时投入情况和对资源的利用效率。较高的消耗工时数可能需要审查工作流程和资源分配，以提高工作效率和进度控制。', '所有日志记录的工时之和\n记录时间在某年', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('hour', 'global', 'effort', '按全局统计的年度投入总人天', 'day_of_annual_effort', '人天', '按全局统计的年度投入总人天是指团队总共投入的工作天数。该度量项可以用来评估人力资源投入情况。投入总人天的增加可能意味着项目投入的工作时间和资源的增加。', '复用：\n按全局统计的年度日志记录的工时总数\n公式：\n按全局统计的年度投入总人天=按按全局统计的年度日志记录的工时总数/后台配置的每日可用工时', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'global', 'doc', '按全局统计的文档总数', 'count_of_doc', '个', '按全局统计的文档总数是指系统或组织中存在的所有文档数量的统计值。它反映了整体文档管理的规模和复杂度。文档总数越大，代表着组织的信息量越丰富，也可能意味着需要更多的资源来维护和管理这些文档。', '所有文档个数求和\n过滤已删除的文档', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'global', 'doc', '按全局统计的年度新增文档个数', 'count_of_annual_created_doc', '个', '按全局统计的年度新增文档个数是指在某年度系统或组织中新建的文档数量。它反映了组织中信息产生的速度和增长的趋势。年度新增文档个数越大，说明组织的信息需求和创造力较强，也可能需要投入更多的资源来管理和维护这些新增文档。该度量项还可以用于评估组织的创新能力和知识管理水平。', '所有文档个数求和\n创建时间为某年\n过滤已删除的文档', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'global', 'project', '按全局统计的年度完成项目数', 'count_of_annual_finished_project', '个', '按全局统计的年度完成项目数是指在某年度完成并关闭的项目数量。这个度量项可以帮助团队了解某年度项目的执行情况和成果，并进行项目交付能力的评估。较高的年度完成项目数表明团队在项目交付方面具有较高的效率。', '所有的项目个数求和\n实际完成时间为某年\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'global', 'story', '按全局统计的每周完成研发需求规模数', 'scale_of_weekly_finished_story', 'sp/工时/功能点', '按全局统计的每周完成研发需求规模数表示每周完成的研发需求的数量。该度量项反映了组织每周完成的研发需求数量，可以用于评估组织的研发需求完成能力和绩效。', '所有的研发需求个数求和\n关闭时间为某周\n关闭原因为已完成\n过滤已删除的研发需求\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'global', 'story', ' 按全局统计的每周完成需求数', 'count_of_weekly_finished_story', '个', '按全局统计的每周完成需求数是指每周已关闭且关闭原因为已完成的研发需求数量。这个度量项可以反映团队在每周的开发效率和成果。完成研发需求数量的增加说明团队在该年度内取得了更多的开发成果和交付物。', '所有研发需求的个数求和\n关闭时间在某周\n关闭原因为已完成\n过滤已删除的研发需求\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'global', 'release', '按全局统计的每周新增发布数', 'count_of_weekly_created_release', '个', '按全局统计的每周新增发布数表示每周新增加的发布数量。该度量项反映了组织每周增加的发布数量，可以用于评估组织产品发布的速度和规模。', '所有的发布个数求和\n发布时间为某周\n过滤已删除的发布\n过滤已删除的产品', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'project', 'story', '按项目统计的研发需求总数', 'count_of_story_in_project', '个', '按项目统计的研发需求总数是指项目中创建或关联的所有研发需求的数量。该度量项反映了项目的规模和复杂度，为项目计划和资源分配提供了参考。', '项目中研发需求个数求和\n过滤已删除的研发需求\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'project', 'story', '按项目统计的已关闭研发需求数', 'count_of_closed_story_in_project', '个', '按项目统计的已关闭研发需求数是指项目中已经关闭的研发需求的数量。该度量项反映了项目中已经关闭的研发需求的数量，可以用于评估项目的研发需求管理绩效和成果。', '项目中研发需求个数求和\n过滤已删除的研发需求\n状态为已关闭\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'project', 'story', '按项目统计的未关闭研发需求数', 'count_of_unclosed_story_in_project', '个', '按项目统计的未关闭研发需求数是指项目中未关闭的研发需求的数量。这个度量项可以反映项目团队在开发过程中的进行中的任务和计划。未关闭研发需求数越多，说明项目团队未完成的开发工作越多，需要进一步跟进从而完成。', '复用：\n按项目统计的研发需求总数\n按项目统计的已关闭研发需求数\n公式：\n按项目统计的关闭研发需求数=按项目统计的研发需求总数-按项目统计的已关闭研发需求数', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'project', 'story', '按项目统计的已完成研发需求数', 'count_of_finished_story_in_project', '个', '按项目统计的已完成研发需求数是指状态为已关闭且关闭原因为已完成的研发需求的数量。这个度量项可以反映项目团队在开发过程中的进展和交付能力。已完成研发需求数越多，说明项目团队在该时间段内取得了更多的开发成果。', '项目中研发需求的个数求和\n状态为已关闭\n关闭原因为已完成\n过滤已删除的研发需求\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'project', 'story', '按项目统计的无效研发需求数', 'count_of_invalid_story_in_project', '个', '按项目统计的无效研发需求数是指被判定为无效的研发需求数量。无效需求可能包括重复需求、不可实现的需求、或者与项目策略和目标不符的需求。通过对无效需求的统计，可以帮助项目团队优化需求管理和筛选机制，以提高需求有效性和资源利用率。较高的无效需求数量可能需要对需求收集和评估流程进行改进。', '项目中研发需求的个数求和\n关闭原因为重复、不做、设计如此\n过滤已删除的研发需求\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'project', 'story', '按项目统计的有效研发需求数', 'count_of_valid_story_in_project', '个', '按项目统计的有效研发需求数是指被确认为有效的研发需求数量。有效需求指的是符合项目策略和目标，可以实施并且对用户有价值的需求。通过对有效需求的统计，可以帮助项目团队评估项目需求的质量和重要性，并进行优先级排序和资源分配。较高的有效需求数量通常表示项目的功能和特性满足了用户和市场的期望，有利于实现项目的成功交付和用户满意度。', '复用：\n按项目统计的无效研发需求数\n按项目统计的研发需求总数\n公式：\n按执行统计的有效研发需求数=按执行统计的研发需求总数-按执行统计的无效研发需求数', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'project', 'story', '按项目统计的所有研发需求规模数', 'scale_of_story_in_project', 'sp/工时/功能点', '按项目统计的所有研发需求规模数表示研发需求的规模总数。该度量项反映了项目研发需求的规模总数，可以用于评估项目团队的研发需求规模管理和成果。', '项目中研发需求的规模数求和\n过滤已删除的研发需求\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('rate', 'project', 'story', '按项目统计的研发需求完成率', 'rate_of_finished_story_in_project', '%', '按项目统计的研发需求完成率表示按项目统计的已完成的研发需求数相对于按项目统计的有效研发需求数。这个度量项衡量了项目研发团队完成需求的能力。完成率越高，代表项目研发团队能够将需求交付给用户，实现正常发布。', '复用：\n按项目统计的已完成研发需求数\n按项目统计的有效研发需求数\n公式：\n按项目统计的研发需求完成率=按项目统计的已完成研发需求数/按项目统计的有效研发需求数*100%', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('time', 'project', 'project', '按项目统计的计划工期', 'planned_period_of_project', '天', '按项目统计的计划工期是基于项目计划和排期制定的预估工期。该度量项通过确定项目开始和结束日期之间的时间间隔来计算。计划工期用于制定项目的时间目标和进度安排，为项目管理提供了基准。与实际工期进行比较，可以评估项目的进展和时间规划的准确性，帮助团队及时调整工作计划。', '计划完成日期-计划开始日期\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('time', 'project', 'project', '按项目统计的剩余工期', 'left_period_of_project', '天', '按项目统计的剩余工期表示项目在当前时间点上还剩下的工作时间。这个度量项可以帮助团队评估项目的剩余工作量和进度。通过比较剩余工期和剩余工时，可以预测项目是否能够按时完成，并采取适当的措施来调整进度，以确保项目的成功交付。', '已关闭的项目：0\n未关闭的项目：\n计划截止日期-当前日期\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'project', 'task', '按项目统计的任务总数', 'count_of_task_in_project', '个', '按项目统计的任务总数是指整个项目当前存在的任务总量。该度量项可以用来跟踪任务的规模和复杂性，为资源分配和工作计划提供基础。较大的任务总数可能需要更多的资源和时间来完成，而较小的任务总数可能意味着项目负荷较轻或项目进展较好。', '项目中所有的任务个数求和\n过滤已删除的任务\n过滤已删除执行的任务\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'project', 'task', '按项目统计的未开始任务数', 'count_of_wait_task_in_project', '个', '按项目统计的未开始任务数指的是在项目执行过程中未开始进行的任务数量。这个度量项帮助团队了解项目进展的一部分，即有多少任务未启动。通过统计未开始任务数，团队可以评估项目的准备状况、资源分配以及可能存在的延迟因素。', '项目中任务个数求和\n状态为未开始\n过滤已删除的任务\n过滤已删除执行的任务\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'project', 'task', '按项目统计的进行中任务数', 'count_of_doing_task_in_project', '个', '按项目统计的进行中任务数表示项目执行过程中正在进行的任务数量。这个度量项帮助团队了解项目当前的工作负载和进展情况。统计进行中任务数可以帮助团队判断项目的工作量是否合理分配，并进行进一步的资源规划和调整。', '项目中任务个数求和\n状态为进行中\n过滤已删除的任务\n过滤已删除执行的任务\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'project', 'task', '按项目统计的已完成任务数', 'count_of_finished_task_in_project', '个', '按项目统计的已完成任务数是指项目已经完成的任务总量。该度量项可以衡量任务完成的进度和效率，以及项目的工作质量和产出。较高的已完成任务总数可能表明项目在交付工作方面表现出较好的能力。', '项目中任务个数求和\n状态为已完成\n过滤已删除的任务\n过滤已删除执行的任务\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('hour', 'project', 'task', '按项目统计的任务预计工时数', 'estimate_of_task_in_project', 'h', '按项目统计的任务预计工时数是指在项目管理中，对所有任务的预计工时进行统计和汇总的度量。这个度量项用于评估项目的工作量和资源需求，并帮助规划和安排项目团队。任务预计工时数是通过对每个任务的工作量估算进行累加而得，可以作为项目计划和进度控制的依据。', '项目中任务的预计工时数求和\n过滤已删除的任务\n过滤已删除执行的任务\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('hour', 'project', 'task', '按项目统计的任务消耗工时数', 'consume_of_task_in_project', 'h', '按项目统计的任务消耗工时数是指已经花费的工时总和，用于完成所有任务。该度量项可以用来评估项目在任务执行过程中的工时投入情况，以及在完成任务方面的效率和资源利用情况。较高的任务消耗工时总数可能表明需要审查工作流程和资源分配，以提高工作效率。', '项目中任务的消耗工时数求和\n过滤已删除的任务\n过滤已删除执行的任务\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('hour', 'project', 'task', '按项目统计的任务剩余工时数', 'left_of_task_in_project', 'h', '按项目统计的任务剩余工时数是指当前未消耗的工时总和，用于完成所有任务。该度量项可以用来评估项目在任务执行过程中剩余的工作量和时间，以及为完成任务所需的资源和计划。较小的任务剩余工时总数可能表示项目将及时完成任务，而较大的任务剩余工时总数可能需要重新评估进度和资源分配。', '项目中任务的剩余工时数求和\n过滤已删除的任务\n过滤已删除执行的任务\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('rate', 'project', 'task', '按项目统计的任务进度', 'progress_of_task_in_project', '%', '按项目统计的任务进度是指项目团队按已消耗的工时数与已消耗和剩余的工时数的比率。这个度量项能够反映项目进度的准确性和任务执行的效率。', '复用：\n按项目统计的任务消耗工时数\n按项目统计的任务剩余工时数\n公式：\n按项目统计的任务进度=按项目统计的任务消耗工时数/（按项目统计的任务消耗工时数+按项目统计的任务剩余工时数）', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('hour', 'project', 'effort', '按项目统计的项目内所有消耗工时数', 'consume_of_all_in_project', 'h', '按项目统计的项目内所有消耗工时数是指项目实际花费的总工时数。该度量项可以用来评估项目的工时投入情况和对资源的利用效率。较高的消耗工时数可能需要审查工作流程和资源分配，以提高工作效率和进度控制。', '项目中所有日志记录的工时之和\n记录时间在某年\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('hour', 'project', 'effort', '按项目统计的已投入人天', 'day_of_invested_in_project', '人天', '按项目统计的已投入人天是指项目总共投入的工作天数。该度量项可以用来评估项目的人力资源投入情况。投入总人天的增加可能意味着项目投入的工作时间和资源的增加。', '复用：\n按项目统计的日志记录的工时总数\n公式：\n按项目统计的已投入人天=按项目统计的项目内所有消耗工时数/后台配置的每日可用工时', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('hour', 'project', 'task', '按瀑布项目统计的已完成任务工作的预计工时(EV)', 'ev_of_finished_task_in_waterfall', 'h', '按瀑布项目统计的已完成任务工作的预计工时指的是在瀑布项目管理方法中，已经完成的任务的预计工时。这个度量项用来评估项目进展与实际完成情况的一致性。EV的值越高，代表项目团队在按计划完成任务的工作量方面表现得越好。', '复用：\n按项目统计的任务进度\n按项目统计的任务预计工时数\n公式：\n按项目统计的已完成任务工作的预计工时(EV)=按项目统计的任务预计工时数*按项目统计的任务进度\n要求项目为瀑布项目\n过滤已删除的任务\n过滤已取消的任务\n过滤已删除执行下的任务\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('hour', 'project', 'effort', '按瀑布项目统计的实际花费工时(AC)', 'ac_of_all_in_waterfall', 'h', '按瀑布项目统计的实际花费工时指的是在瀑布项目管理方法中，实际花费的工时总数。这个度量项用于评估实际工作量和预计工作量之间的差异，有助于估计项目的真实进展情况。AC的值越接近EV，代表项目团队在任务执行方面表现得越好。', '瀑布项目中所有日志记录的工时之和\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('hour', 'project', 'task', '按瀑布项目统计的任务的计划完成工时(PV)', 'pv_of_task_in_waterfall', 'h', '按瀑布项目统计的任务的计划完成工时指的是在瀑布项目管理方法中，按计划需要完成的任务的总预计工时。这个度量项用于评估任务的预期工作量，可用作与实际花费工时和已完成任务的预计工时进行比较。', '瀑布项目中所有任务的预计工时之和\n过滤已删除的任务\n过滤已取消的任务\n过滤已删除的执行的任务\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('rate', 'project', 'task', '按瀑布项目统计的进度偏差率', 'sv_in_waterfall', '%', '按瀑布项目统计的进度偏差率是用来衡量项目当前的进度与计划进度之间的差异。它通过计算已完成的工作量与计划工作量之间的差异来评估项目的进展情况。', '复用：\n按瀑布项目统计的已完成任务工作的预计工时(EV)\n按瀑布项目统计的任务的计划完成工时(PV)\n公式：\n按瀑布项目统计的进度偏差率=(EV-PV)/PV*100%', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('rate', 'project', 'task', '按瀑布项目统计的成本偏差率', 'cv_in_waterfall', '%', '按瀑布项目统计的成本偏差率用于衡量项目的实际成本与计划成本之间的差异。它通过计算已花费的成本与预计花费的成本之间的差异来评估项目的成本绩效。', '复用：\n按瀑布项目统计的已完成任务工作的预计\n按瀑布项目统计的实际花费工时(AC)\n公式：\n按瀑布项目统计的成本偏差率=(EV-AC)/AC*100%', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'project', 'bug', '按项目统计的Bug总数', 'count_of_bug_in_project', '个', '按项目统计的Bug总数是指在项目中发现的所有Bug的数量。这个度量项反映了项目的整体Bug质量情况。Bug总数越多可能代表项目的代码质量存在问题，需要进行进一步的解决和改进。', '项目中Bug个数求和\n过滤已删除的Bug\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'project', 'bug', '按项目统计的激活Bug数', 'count_of_activated_bug_in_project', '个', '按项目统计的激活Bug数是指当前未解决的Bug数量。这个度量项反映了项目当前存在的待解决问题数量。激活Bug总数越多可能代表项目的稳定性较低，需要加强Bug解决的速度和质量。', '项目中Bug个数求和\n状态为激活\n过滤已删除的Bug\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'project', 'bug', '按项目统计的已关闭Bug数', 'count_of_closed_bug_in_project', '个', '按项目统计的已关闭Bug总数是指已经被关闭的Bug数量。这个度量项反映了项目中已经关闭的缺陷数量。已关闭Bug总数的增加说明项目进行了持续的改进和修复工作。 ', '项目中Bug个数求和\n状态为已关闭\n过滤已删除的Bug\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'project', 'risk', '按项目统计的开放的风险数', 'count_of_opened_risk_in_project', '个', '按项目统计的开放的风险数是指在项目管理中，正在被跟踪和管理的项目风险的数量。风险是项目中潜在的不确定事件或情况，可能对项目目标的达成产生负面影响。通过跟踪和管理项目风险，项目团队可以及时采取措施降低风险的概率和影响程度。', '项目中风险的个数求和\n状态为开放\n过滤已删除的风险\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'project', 'issue', '按项目统计的开放的问题数', 'count_of_opened_issue_in_project', '个', '按项目统计的开放的问题数指的是在项目管理中，正在被跟踪和解决的项目问题的数量。问题是指在项目执行过程中遇到的障碍、困难或需要解决的事项。通过跟踪和解决项目问题，可以避免问题的积累和对项目目标的影响。', '项目中问题的个数求和\n状态为开放\n过滤已删除的问题\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'project', 'user', '按项目统计的人员总数', 'count_of_user_in_project', '个', '按项目统计的人员总数是指参与项目的全部人员的数量。这个度量项用于了解项目团队的规模和组成，对项目资源的分配和管理起到重要作用。', '项目中团队成员个数求和\n过滤已移除的人员\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('hour', 'execution', 'task', '按执行统计的任务预计工时数', 'estimate_of_task_in_execution', 'h', '按执行统计的任务预计工时数是指在执行管理中，对所有任务的预计工时进行统计和汇总的度量。该度量项反映了任务的预计复杂性和所需的资源投入，可以帮助团队管理者评估任务的难度并安排资源。', '执行中任务的预计工时数求和\n过滤已删除的任务\n过滤已删除的执行\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('hour', 'execution', 'task', '按执行统计的任务消耗工时数', 'consume_of_task_in_execution', 'h', '按执行统计的任务消耗工时数是指已经花费的工时总和，用于完成所有任务。该度量项反映了任务的实际完成情况和资源使用情况，可以帮助团队掌握任务的进展情况和资源利用效率。', '执行中任务的消耗工时数求和\n过滤已删除的任务\n过滤已删除的执行\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('hour', 'execution', 'task', '按执行统计的任务剩余工时数', 'left_of_task_in_execution', 'h', '按执行统计的任务剩余工时数是指当前未消耗的工时总和，用于完成所有任务。该度量项反映了任务完成的剩余工作量，可以帮助团队预测任务的完成时间和资源需求。', '执行中任务的剩余工时数求和\n过滤已删除的任务\n过滤已删除的执行\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('rate', 'execution', 'task', '按执行统计的任务进度', 'progress_of_task_in_execution', '%', '按执行统计的任务进度是指执行团队按已消耗的工时数与已消耗和剩余的工时数的比率。该度量项反映了任务的执行进展情况，可以帮助团队评估任务是否按计划进行并做出相应调整。', '复用：\n按执行统计的任务消耗工时数\n按执行统计的任务剩余工时数\n公式：\n按执行统计的任务进度=按执行统计的任务消耗工时数/（按执行统计的任务消耗工时数+按执行统计的任务剩余工时数）', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'execution', 'task', '按执行统计的任务总数', 'count_of_task_in_execution', '个', '按执行统计的任务总数是指整个执行当前存在的任务总量。该度量项可以用来跟踪任务的规模和复杂性，为资源分配和工作计划提供基础，可以帮助团队评估工作负荷和任务分配的合理性。', '执行中所有的任务个数求和\n过滤已删除的任务\n过滤已删除的执行\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'execution', 'task', '按执行统计的已完成任务数', 'count_of_finished_task_in_execution', '个', '按执行统计的已完成任务数是指执行已经完成的任务总量。该度量项可以衡量任务完成的进度和效率，以及项目的工作质量和产出。较高的已完成任务总数可能表明项目在交付工作方面表现出较好的能力。', '执行中任务个数求和\n状态为已完成\n过滤已删除的任务\n过滤已删除的执行\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'execution', 'task', '按执行统计的未完成任务数', 'count_of_unfinished_task_in_execution', '个', '按执行统计的未完成任务数是指执行未完成的任务总量。该度量项反映了团队的待办工作量和未来的工作压力。较低的未完成任务总数可能表明项目在交付工作方面表现出较好的能力。', '复用：\n按执行统计的未完成任务数\n按执行统计的任务总数\n公式：\n按执行统计的未完成任务数=按执行统计的任务总数-按执行统计的已完成任务数', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'execution', 'task', '按执行统计的日完成任务数', 'count_of_daily_finished_task', '个', '按执行统计的日完成任务数是指每天完成的任务数量。该度量项反映了团队的日常工作效率和任务完成速度。', '执行中任务个数求和\n状态为已完成\n实际完成日期为某日\n过滤已删除的任务\n过滤已删除的执行\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'execution', 'story', '按执行统计的研发需求总数', 'count_of_story_in_execution', '个', '按执行统计的研发需求总数是指执行中创建和关联的所有研发需求的数量。该度量项反映了执行的规模和复杂度，为执行计划和资源分配提供了参考。', '执行中研发需求个数求和\n过滤已删除的研发需求\n过滤已删除的执行\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'execution', 'story', '按执行统计的已完成研发需求数', 'count_of_finished_story_in_execution', '个', '按执行统计的已完成研发需求数是指状态为已关闭且关闭原因为已完成的研发需求的数量。这个度量项可以反映执行团队在开发过程中的进展和交付能力。已完成研发需求数越多，说明执行团队在该时间段内取得了更多的开发成果。', '执行中研发需求的个数求和\n状态为已关闭\n关闭原因为已完成\n过滤已删除的研发需求\n过滤已删除的执行\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'execution', 'story', '按执行统计的无效研发需求数', 'count_of_invalid_story_in_execution', '个', '按执行统计的无效研发需求数是指被判定为无效的研发需求数量。无效需求可能包括重复需求、不可实现的需求、或者与项目策略和目标不符的需求。通过对无效需求的统计，可以帮助执行团队优化需求管理和筛选机制，以提高需求有效性和资源利用率。较高的无效需求数量可能需要对需求收集和评估流程进行改进。', '执行中研发需求的个数求和\n关闭原因为重复、不做、设计如此和已取消\n过滤已删除的研发需求\n过滤已删除的执行\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'execution', 'story', '按执行统计的有效研发需求数', 'count_of_valid_story_in_execution', '个', '按执行统计的有效研发需求数是指被确认为有效的研发需求数量。有效需求指的是符合项目策略和目标，可以实施并且对用户有价值的需求。通过对有效需求的统计，可以帮助执行团队评估项目需求的质量和重要性，并进行优先级排序和资源分配。较高的有效需求数量通常表示执行的功能和特性满足了用户和市场的期望，有利于实现项目的成功交付和用户满意度。', '复用：\n按执行统计的无效研发需求数\n按执行统计的研发需求总数\n公式：\n按执行统计的有效研发需求数=按执行统计的研发需求总数-按执行统计的无效研发需求数', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('rate', 'execution', 'story', '按执行统计的研发需求完成率', 'rate_of_finished_story_in_execution', '%', '按执行统计的研发需求完成率表示按执行统计的已完成的研发需求数相对于按执行统计的有效研发需求数。这个度量项衡量了执行研发团队完成需求的能力。', '复用：\n按执行统计的已完成研发需求数\n按执行统计的有效研发需求数\n公式：\n按执行统计的研发需求完成率=按执行统计的已完成研发需求数/按执行统计的有效研发需求数*100%', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
