INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'user', 'feedback', '按人员统计的待处理反馈数', 'count_of_assigned_feedback_in_user', '个', '按人员统计的待处理反馈数表示每个人待处理的反馈数量之和。反映了每个人需要处理的反馈数量上的规模。该数值越大，说明需要投入越多的时间处理反馈。', '所有反馈个数求和\n指派给为某人\n过滤已删除的反馈\n过滤已删除产品的反馈', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'project', 'story', '按项目统计的年度完成研发需求数', 'count_of_annual_finished_story_in_project', '个 ', '按项目统计的年度完成研发需求数是指在某年度已关闭且关闭原因为已完成的研发需求数量。这个度量项可以反映项目团队在某年度的开发效率和成果。完成研发需求数量的增加说明项目团队在该年度内取得了更多的开发成果和交付物。', '项目中研发需求的个数求和\n关闭时间在某年\n关闭原因为已完成\n过滤已删除的研发需求\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('scale', 'project', 'story', '按项目统计的年度完成研发需求规模数', 'scale_of_annual_finished_story_in_project', 'sp/工时/功能点', '按项目统计的年度完成研发需求数是指在某年度已关闭且关闭原因为已完成的研发需求规模数。这个度量项可以反映项目团队在某年度的开发效率和成果。完成研发需求规模数的增加说明项目团队在该年度内取得了更多的开发成果和交付物。', '项目中研发需求的规模数求和\n关闭时间在某年\n关闭原因为已完成\n过滤已删除的研发需求\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('time', 'project', 'project', '按项目统计的的实际工期', 'count_of_actual_time_in_project', '天', '按项目统计的实际工期反映了项目在执行过程中实际花费的时间。该度量项通过统计项目实际的开始和完成日期来计算。实际工期的准确记录能够帮助团队评估项目的执行效率和时间管理能力。较短的实际工期可能意味着项目按计划进行，团队高效执行，而较长的实际工期可能表明项目存在一些延迟和挑战。', '已关闭的项目：\n实际完成日期-实际开始日期\n未关闭的项目：\n当前日期-实际开始日期\n过滤已删除的项目', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
INSERT INTO `zt_metric`(`purpose`, `scope`, `object`, `name`, `code`, `unit`, `desc`, `definition`, `when`, `event`, `cronCFG`, `time`, `createdBy`, `createdDate`, `editedBy`, `editedDate`, `order`, `deleted`) VALUES ('time', 'project', 'project', '按项目统计的的工期偏差', 'variance_of_time_in_project', '天', '按项目统计的工期偏差表示实际工期与计划工期之间的差异。工期偏差的正值表示项目进度延迟，负值表示项目进度提前。工期偏差可以帮助团队及时识别项目进度的偏差，并采取相应的调整措施来重新规划资源和工作计划，以确保项目能够按时完成。', '复用：\n按项目统计的实际工期\n按项目统计的计划工期\n公式：\n按项目统计的工期偏差=按项目统计的实际工期-按项目统计的计划工期', 'realtime', '', '', '', '', NULL, '', NULL, 0, '0');
